def telem_eqns(eqns):
    call = "YD0BCX-13"
    call = (call + " " * (9 - len(call)))[:9]
    eqns = eqns.split()
    coefficients = eqns[1:]

    eqns_message = f":{call}:EQNS." + ",".join(coefficients)
    return eqns_message

